mapper.select:
SELECT COUNT(*) FROM (SELECT A.RECEIPT_ID , A.R_TEL , A.FLAG_BROD , TO_CHAR(TO_DATE(A.RECEIPT_TIME , 'HH24MI') , 'HH24:MI') AS RECEIPT_TIME , A.report_type , B.BAS_NAME , A.INDIVIDUAL_NAME , C.TYPE_NAME , A.MEMO , A.RECEIPT_DAY , D.AREA_NAME , A.FLAG_IMPORTANT FROM REPORT_RECEIPT_TODAY A LEFT OUTER JOIN CODE_REPORT_TYPE B ON A.REPORT_TYPE = B.BAS_SCOD LEFT OUTER JOIN CODE_INFORMER_TYPE C ON A.INDIVIDUAL_TYPE = C.TYPE_ID LEFT OUTER JOIN CODE_AREACODE D ON A.REGION_ID = D.AREA_CODE WHERE B.BAS_LCOD = '100' AND RECEIPT_DAY= to_char(sysdate,'YYYYMMDD')
AND REGION_ID = #{AREA_CODE}
AND FLAG_SEND = 'Y')

SELECT * FROM(SELECT ROWNUM AS SEQ, H.* FROM(SELECT ROW_NUMBER() OVER(ORDER BY RECEIPT_DT ASC) AS RNUM, G.* FROM(SELECT A.RECEIPT_ID ,REGEXP_REPLACE(A.R_TEL ,'([[:digit:]]{'|| DECODE(SUBSTR(A.R_TEL,0,2 ),'02',2,3)||'})([[:digit:]]{3,4})([[:digit:]]{4})','\1-\2-\3') AS R_TEL , A.FLAG_BROD , TO_CHAR(TO_DATE(A.RECEIPT_TIME , 'HH24MI') , 'HH24:MI') AS RECEIPT_TIME , A.RECEIPT_DAY||A.RECEIPT_TIME AS RECEIPT_DT , to_char(to_date(broad_time , 'YYYYMMDDHH24MISS'),'HH24:MI') AS BROAD_TIME , A.report_type , B.BAS_NAME , A.INDIVIDUAL_NAME , C.TYPE_NAME , A.MEMO , A.RECEIPT_DAY , A.FLAG_IMPORTANT , B.F_COLOR , B.B_COLOR , D.CITY AS AREA_NAME FROM (SELECT * FROM REPORT_RECEIPT_TODAY WHERE 1 = 1
AND REPORT_TYPE = #{REPORT_TYPE}
AND REPORT_TYPE2 = #{REPORT_TYPE2}
AND REGION_ID= #{AREA_CODE}
AND INDIVIDUAL_TYPE = #{INDIVIDUAL_TYPE}
AND FLAG_BROD = 'N'
AND FLAG_BROD <> 'N'
AND FLAG_IMPORTANT = 'N'
AND FLAG_IMPORTANT <> 'N'
AND MEMO LIKE '%'||#{CONTENT}||'%'
) A LEFT OUTER JOIN CODE_REPORT_TYPE B ON A.REPORT_TYPE = B.BAS_SCOD LEFT OUTER JOIN CODE_INFORMER_TYPE C ON A.INDIVIDUAL_TYPE = C.TYPE_ID LEFT OUTER JOIN CODE_AREACODE D ON A.REGION_ID = D.AREA_CODE WHERE RECEIPT_DAY= to_char(sysdate,'YYYYMMDD') AND B.BAS_LCOD = '100' AND FLAG_SEND = 'Y' ) G ORDER BY RNUM DESC ) H )
WHERE SEQ > #{startRow} AND SEQ <= (#{startRow} + #{size})

        SELECT COUNT(*) FROM 
        	(
        	SELECT * FROM REPORT_RECEIPT_TODAY
                WHERE 1 = 1
                AND FLAG_SEND = 'Y'
                AND RECEIPT_DAY= to_char(sysdate,'YYYYMMDD')

                	
                	AND REPORT_TYPE = #{REPORT_TYPE}
                	
                	
                    AND REPORT_TYPE2 = #{REPORT_TYPE2}
                    
                    
                    AND REGION_ID= #{AREA_CODE}
                    
                    
                    AND INDIVIDUAL_TYPE = #{INDIVIDUAL_TYPE}
                    
                    
                    
                        
                            AND FLAG_BROD = 'N'
                        
                    	
                    	    AND FLAG_BROD <> 'N'
                    	
                    
                    
                    
                        
                            AND FLAG_IMPORTANT = 'N'
                        
                    	
                    	    AND FLAG_IMPORTANT <> 'N'
                    	
                    
                    
                    
                    AND MEMO LIKE '%'||#{CONTENT}||'%'
                    

            	)

SELECT BROAD_TIME 
FROM REPORT_RECEIPT_TODAY 
WHERE RECEIPT_ID = #{RECEIPT_ID}

mapper.update:
        DECLARE BEGIN
        
	        UPDATE REPORT_RECEIPT_TODAY
	        SET FLAG_BROD = #{item.FLAG_BROAD}
	        WHERE RECEIPT_ID = #{item.RECEIPT_ID}
           
	    ; END;
