mapper.select:
SELECT T.* FROM ( SELECT J2.* ,NVL(D.ORG_NAME,'무소속') AS ORG_NAME FROM ( SELECT J1.* ,C.TYPE_NAME AS INFORMER_TYPE_NAME FROM ( SELECT A.INFORMER_ID ,A.ACT_ID ,A.INFORMER_NAME ,A.AREA_CODE ,REPLACE(B.AREA_NAME,'교통방송','') AS AREA_NAME
,CASE WHEN PHONE_CELL IS NULL OR PHONE_CELL = '' THEN CASE WHEN PHONE_HOME IS NULL OR PHONE_HOME = '' THEN REGEXP_REPLACE(PHONE_OFFICE, '(02|.{3})(.+)(.{4})', '\1-\2-\3') ELSE REGEXP_REPLACE(PHONE_HOME, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END ELSE REGEXP_REPLACE(phone_cell, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END AS phone_cell
,REGEXP_REPLACE(phone_cell, '(02|.{3})(.+)(.{4})', '\1-\2-\3') as phone_cell2
,A.INFORMER_TYPE ,A.ORG_ID ,A.FLAG_ACT ,A.REG_DATE ,NVL(A.ADDRESS_HOME,'미입력') as ADDRESS_HOME FROM INFORMER A LEFT JOIN CODE_AREACODE B ON A.AREA_CODE=B.AREA_CODE )J1 LEFT JOIN CODE_INFORMER_TYPE C ON J1.informer_type=C.TYPE_ID )J2 LEFT JOIN ORGANIZATION D ON J2.AREA_CODE=D.AREA_CODE AND J2.ORG_ID=D.ORG_ID AND J2.INFORMER_TYPE=D.IND_TYPE )T WHERE 1=1
AND T.INFORMER_ID=#{informerId}
AND T.AREA_CODE=#{areaCode}
AND T.informer_type=#{informerType}
AND T.ORG_ID=#{orgId}
AND T.FLAG_ACT=#{flagAct}
AND T.ACT_ID LIKE '%'||#{searchValue}||'%'
AND T.INFORMER_NAME LIKE '%'||#{searchValue}||'%'
AND T.phone_cell LIKE '%'||#{searchValue}||'%'
AND T.REG_DATE BETWEEN #{sDate} AND TO_DATE(#{eDate}, 'YYYY-MM-DD')+1
ORDER BY T.REG_DATE DESC nulls last

SELECT T.* FROM ( SELECT J2.* ,NVL(D.ORG_NAME,'무소속') AS ORG_NAME FROM ( SELECT J1.* ,C.TYPE_NAME AS INFORMER_TYPE_NAME FROM ( SELECT A.INFORMER_ID ,NVL(A.ACT_ID , '정보없음') as ACT_ID ,NVL(A.INFORMER_NAME, '정보없음') as INFORMER_NAME ,A.AREA_CODE ,REPLACE(B.AREA_NAME,'교통방송','') AS AREA_NAME
,CASE WHEN PHONE_CELL IS NULL OR PHONE_CELL = '' THEN CASE WHEN PHONE_HOME IS NULL OR PHONE_HOME = '' THEN REGEXP_REPLACE(PHONE_OFFICE, '(02|.{3})(.+)(.{4})', '\1-\2-\3') ELSE REGEXP_REPLACE(PHONE_HOME, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END ELSE REGEXP_REPLACE(phone_cell, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END AS phone_cell
,A.INFORMER_TYPE ,A.ORG_ID ,NVL(A.FLAG_ACT, '정보없음') as FLAG_ACT ,NVL(A.REG_DATE, '정보없음') as REG_DATE ,NVL(A.ADDRESS_HOME,'미입력') as ADDRESS_HOME FROM INFORMER A LEFT JOIN CODE_AREACODE B ON A.AREA_CODE=B.AREA_CODE )J1 LEFT JOIN CODE_INFORMER_TYPE C ON J1.informer_type=C.TYPE_ID )J2 LEFT JOIN ORGANIZATION D ON J2.AREA_CODE=D.AREA_CODE AND J2.ORG_ID=D.ORG_ID AND J2.INFORMER_TYPE=D.IND_TYPE )T WHERE 1=1
AND T.INFORMER_ID=#{informerId}
AND T.AREA_CODE=#{areaCode}
AND T.informer_type=#{informerType}
AND T.ORG_ID=#{orgId}
AND T.FLAG_ACT=#{flagAct}
AND T.ACT_ID LIKE '%'||#{searchValue}||'%'
AND T.INFORMER_NAME LIKE '%'||#{searchValue}||'%'
AND T.phone_cell LIKE '%'||#{searchValue}||'%'
AND T.REG_DATE BETWEEN #{sDate} AND #{eDate}
AND ROWNUM <= 20
ORDER BY T.REG_DATE DESC nulls last

SELECT T.* FROM ( SELECT T.*, ROWNUM AS RN FROM ( SELECT J2.*, NVL(D.ORG_NAME, '무소속') AS ORG_NAME FROM ( SELECT J1.*, C.TYPE_NAME AS INFORMER_TYPE_NAME FROM ( SELECT A.INFORMER_ID, NVL(A.ACT_ID, '정보없음') as ACT_ID, NVL(A.INFORMER_NAME, '정보없음') as INFORMER_NAME, A.AREA_CODE, REPLACE(B.AREA_NAME, '교통방송', '') AS AREA_NAME, CASE WHEN PHONE_CELL IS NULL OR PHONE_CELL = '' THEN CASE WHEN PHONE_HOME IS NULL OR PHONE_HOME = '' THEN REGEXP_REPLACE(PHONE_OFFICE, '(02|.{3})(.+)(.{4})', '\1-\2-\3') ELSE REGEXP_REPLACE(PHONE_HOME, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END ELSE REGEXP_REPLACE(PHONE_CELL, '(02|.{3})(.+)(.{4})', '\1-\2-\3') END AS PHONE_CELL, A.INFORMER_TYPE, A.ORG_ID, NVL(A.FLAG_ACT, '정보없음') as FLAG_ACT, NVL(A.REG_DATE , '정보없음') as REG_DATE, NVL(A.ADDRESS_HOME, '미입력') AS ADDRESS_HOME FROM INFORMER A LEFT JOIN CODE_AREACODE B ON A.AREA_CODE = B.AREA_CODE ) J1 LEFT JOIN CODE_INFORMER_TYPE C ON J1.INFORMER_TYPE = C.TYPE_ID ) J2 LEFT JOIN ORGANIZATION D ON J2.AREA_CODE = D.AREA_CODE AND J2.ORG_ID = D.ORG_ID AND J2.INFORMER_TYPE = D.IND_TYPE ) T WHERE ROWNUM < #{endRnum}
AND T.INFORMER_ID = #{InfrmVO.informerId}
AND T.AREA_CODE = #{InfrmVO.areaCode}
AND T.informer_type = #{InfrmVO.informerType}
AND T.ORG_ID = #{InfrmVO.orgId}
AND T.FLAG_ACT = #{InfrmVO.flagAct}
AND T.ACT_ID LIKE '%' || #{InfrmVO.searchValue} || '%'
AND T.INFORMER_NAME LIKE '%' || #{InfrmVO.searchValue} || '%'
AND T.phone_cell LIKE '%' || #{InfrmVO.searchValue} || '%'
AND T.REG_DATE BETWEEN #{InfrmVO.sDate} AND #{InfrmVO.eDate}
) T WHERE T.RN >= #{startRnum} ORDER BY T.REG_DATE DESC NULLS LAST

select infrm_div||TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') as informerId 
from code_areacode 
where area_code=#{areaCode}

SELECT NVL(max(TO_NUMBER(ACT_ID)),0)+1 as actId FROM INFORMER
WHERE AREA_CODE=#{areaCode} 

and REGEXP_INSTR(act_id,'[^0-9]')=0

SELECT distinct PHONE_CELL as phoneCell FROM INFORMER
WHERE PHONE_CELL=#{phoneCell}

	AND INFORMER_ID!=#{informerId}

mapper.update:
