         WHERE INFORMER_ID =  #{informerId}

mapper.select:
SELECT UPDATE_TEXT, UPDATE_DATE FROM INFORMER_HISTORY WHERE 1 = 1
AND INFORMER_ID = #{informerId}
ORDER BY INFORMER_HIST_ID DESC

SELECT * FROM ( SELECT T.*, ROWNUM AS RN FROM ( SELECT J2.* , NVL(D.ORG_NAME, '무소속') AS ORG_NAME FROM ( SELECT J1.* , C.TYPE_NAME AS INFORMER_TYPE_NAME FROM ( SELECT A.INFORMER_ID , NVL(A.ACT_ID,'정보없음') AS ACT_ID , NVL(A.INFORMER_NAME,'정보없음') AS INFORMER_NAME , A.AREA_CODE , REPLACE(B.AREA_NAME,'교통방송','') AS AREA_NAME , CASE WHEN A.PHONE_CELL IS NULL OR A.PHONE_CELL = '' THEN CASE WHEN A.PHONE_HOME IS NULL OR A.PHONE_HOME = '' THEN REGEXP_REPLACE(A.PHONE_OFFICE,'(02|.{3})(.+)(.{4})','\1-\2-\3') ELSE REGEXP_REPLACE(A.PHONE_HOME ,'(02|.{3})(.+)(.{4})','\1-\2-\3') END ELSE REGEXP_REPLACE(A.PHONE_CELL ,'(02|.{3})(.+)(.{4})','\1-\2-\3') END AS PHONE_CELL , A.INFORMER_TYPE , A.ORG_ID , NVL(A.FLAG_ACT,'정보없음') AS FLAG_ACT , NVL(A.REG_DATE,'정보없음') AS REG_DATE , NVL(A.ADDRESS_HOME,'미입력') AS ADDRESS_HOME FROM INFORMER A LEFT JOIN CODE_AREACODE B ON A.AREA_CODE = B.AREA_CODE ) J1 LEFT JOIN CODE_INFORMER_TYPE C ON J1.INFORMER_TYPE = C.TYPE_ID ) J2 LEFT JOIN ORGANIZATION D ON J2.AREA_CODE = D.AREA_CODE AND J2.ORG_ID = D.ORG_ID AND J2.INFORMER_TYPE = D.IND_TYPE WHERE 1=1
AND J2.INFORMER_ID = #{vo.informerId}
AND J2.AREA_CODE = #{vo.areaCode}
AND J2.INFORMER_TYPE = #{vo.informerType}
AND J2.ORG_ID = #{vo.orgId}
AND J2.FLAG_ACT = #{vo.flagAct}
AND J2.ACT_ID LIKE '%' || #{vo.searchValue} || '%'
AND J2.INFORMER_NAME LIKE '%' || #{vo.searchValue} || '%'
AND J2.PHONE_CELL LIKE '%' || #{vo.searchValue} || '%'
AND J2.REG_DATE BETWEEN #{vo.sDate} AND #{vo.eDate}
) T WHERE ROWNUM < #{endRnum} ) WHERE RN >= #{startRnum} ORDER BY ${orderBy}

