mapper.select:
SELECT MILE.INFORMER_ID, MILE.ACT_ID, IFM.INFORMER_NAME, NVL(IFM.ORG_NAME,'무소속') AS ORG_NAME, IFM.PHONE_CELL, MILE.ALL_MILEAGE FROM MILEAGE_INFO MILE JOIN INFORMER IFM ON IFM.INFORMER_ID = MILE.INFORMER_ID WHERE IFM.INFORMER_ID = MILE.INFORMER_ID
AND IFM.AREA_CODE=#{areaCode}
AND IFM.informer_type=#{informerType}
AND IFM.ORG_ID=#{orgId}
AND IFM.FLAG_ACT=#{flagAct}
AND IFM.ACT_ID LIKE '%'||#{searchValue}||'%'
AND IFM.INFORMER_NAME LIKE '%'||#{searchValue}||'%'
AND IFM.phone_cell LIKE '%'||#{searchValue}||'%'
ORDER BY MILE.ALL_MILEAGE DESC

SELECT MILE.INFORMER_ID, MILE.ACT_ID, IFM.INFORMER_NAME, NVL(IFM.ORG_NAME,'무소속')AS ORG_NAME, IFM.PHONE_CELL, MILE.CUM_MILEAGE, NVL(GRD.GRADE, '없음')AS GRADE FROM MILEAGE_INFO MILE JOIN INFORMER IFM ON MILE.INFORMER_ID = IFM.INFORMER_ID LEFT JOIN GRADE_INFO GRD ON MILE.INFORMER_ID = GRD.INFORMER_ID WHERE 1=1
AND IFM.AREA_CODE=#{areaCode}
AND IFM.informer_type=#{informerType}
AND IFM.ORG_ID=#{orgId}
AND IFM.FLAG_ACT=#{flagAct}
AND IFM.ACT_ID LIKE '%'||#{searchValue}||'%'
AND IFM.INFORMER_NAME LIKE '%'||#{searchValue}||'%'
AND IFM.phone_cell LIKE '%'||#{searchValue}||'%'
ORDER BY MILE.CUM_MILEAGE DESC

mapper.update:
update mileage_info
set 
    ALL_MILEAGE = ALL_MILEAGE + #{mileList[0].PAYMENT_MILE},
    CUM_MILEAGE = CUM_MILEAGE + #{mileList[0].PAYMENT_MILE}
where INFORMER_ID = #{mileList[0].INFORMER_ID}

UPDATE MILEAGE_INFO
SET ALL_MILEAGE = ALL_MILEAGE - #{MINUS_MILE}
WHERE INFORMER_ID = #{INFORMER_ID}

UPDATE GRADE_INFO
SET CUM_MILEAGE = #{CUM_MILEAGE},
GRADE = #{GRADE}
WHERE INFORMER_ID = #{INFORMER_ID}
