mapper.select:
SELECT A.USER_ID ,A.USER_PW ,A.USER_NAME ,A.REGION_ID ,C.AREA_NAME AS REGION_NAME ,B.AUTH_NAME ,A.USER_TEL ,A.AUTH_CODE ,A.FLAG_USE ,A.MEMO ,A.IN_TEL ,TO_CHAR(A.REG_DT,'YYYY-MM-DD') AS REG_DT ,D.ST ,D.FN ,DECODE(D.DF1,'접속중','접속중','미접속') AS CN_YN FROM SYS_USER A
LEFT JOIN SYS_AUTH B ON A.AUTH_CODE = B.AUTH_CODE
LEFT JOIN CODE_AREACODE C ON A.REGION_ID = C.AREA_CODE
LEFT JOIN (SELECT T1.* ,case when df > 0 then '미접속' else '접속중' end as df1 FROM (SELECT USER_ID ,TO_CHAR(MAX(ST_TIME), 'YYYY-MM-DD HH24:MI:SS') ST ,TO_CHAR(MAX(FN_TIME), 'YYYY-MM-DD HH24:MI:SS') FN
,SIGN(ROUND(( CAST(MAX(FN_TIME) AS DATE) - CAST(MAX(ST_TIME) AS DATE) ) * 24 * 60 * 60)) df
FROM LOGIN_HISTORY where ST_time >sysdate-2 GROUP BY USER_ID ) T1) D ON A.USER_ID = D.USER_ID
WHERE 1=1
AND A.USER_ID=#{userId}
AND A.USER_NAME=#{userName}
AND A.REGION_ID=#{regionId}
AND A.AUTH_CODE not in('999')
AND A.AUTH_CODE=#{authCode}
AND A.USER_ID LIKE '%'||#{searchValue}||'%'
AND A.USER_NAME LIKE '%'||#{searchValue}||'%'
AND A.REG_DT BETWEEN #{sDate} AND #{eDate}
ORDER BY A.REG_DT DESC

mapper.update:
UPDATE SYS_USER
SET

		USER_PW=#{userPw},

	 USER_NAME    = #{userName}
	,REGION_ID    = #{regionId}
	,AUTH_CODE    = #{authCode}
	,USER_TEL     = #{userPhone}
	,MEMO     = #{memo}
WHERE USER_ID=#{userId}

mapper.insert:
INSERT INTO LOGIN_HISTORY(
	USER_ID
	,ST_TIME
)
VALUES (
	#{userId}
	,to_char(sysdate,'YYYY-MM-DD HH24:MI:SS')
)

mapper.update:
UPDATE LOGIN_HISTORY
SET FN_TIME = to_char(sysdate,'YYYY-MM-DD HH24:MI:SS')
WHERE USER_ID=#{userId}
AND ST_TIME = 
(
	select ST_TIME from login_history 
	where user_id =#{userId} 
	and ST_time >sysdate-2
	and fn_time IS NULL		
)

mapper.delete:
delete from login_history
where user_id =#{userId}
and fn_time is null
